import {QueryClient, QueryClientProvider, useQuery} from "react-query";
import Nav from "../Nav";
import Login from "../Login";
import Main from "../Main";
import "./css/inp.css"
const queryClient = new QueryClient();

function OrderEditor() {
    return (
        <div className="d-table-cell flex-wrap">
            <QueryClientProvider client={queryClient}>
                <REQ_Data/>
            </QueryClientProvider>
        </div>
    );
}


function REQ_Data(props) {
    const { isLoading, error, data } = useQuery(
        'repoData',
        () =>
            fetch('http://127.0.0.1:8000/api/LoadData',
                {
                    headers: {
                        'Accept': "application/json",
                        'Content-Type': "application/json",

                    },
                    credentials: 'include',
                }).then((response) => response.json())
    );

    if (isLoading) return <p>Загрузка...</p>;

    if (error) return <p>Ошибка: {error.message}</p>;
    console.log(data)
    const [name, Set_name] = useState('');
    const [count, Set_amount] = useState('');
    function nameChangeHandler(event){
        Set_name(event.target.value)
    };
    function amountChangeHandler(event){
        Set_amount(event.target.value)
    };
    return (
        <div>
            <p1>Добавить товар</p1><tr></tr>
            <input type="text" value={name} className="type-2" placeholder = "Название"/>
            <input type="text" value={count} className="type-2" placeholder = "Цена"/>
            <button className="btn btn-primary d-block w-100">Добавить</button>
        </div>
    );

}
export default OrderEditor;
