import {Authorization} from "./Requests";
import {useState} from "react";
import {
    QueryClient,
    QueryClientProvider,
    useQuery,
} from 'react-query';
import { useAsync } from "react-async"
import Nav from "./Nav";
import Main from "./Main";
const queryClient = new QueryClient();

function Login(props) {
    const [login, Set_login] = useState('');
    const [pas, Set_pas] = useState('');
    function loginChangeHandler(event){
        Set_login(event.target.value)
    };
    function pasChangeHandler(event){
        Set_pas(event.target.value)
    };

    const d = {
        login: login,
        password: pas
    };

    function loginHandler() {
        Authorization(d, props.setauth)
        //console.log(year);
    }

    // function Send() {
    //
    //     const { isLoading, error, data } = useQuery(
    //         'repoData',
    //         () =>
    //             fetch('http://127.0.0.1:8000/api/login',
    //                 {
    //                     method: 'POST', // или 'PUT'
    //                     body: JSON.stringify(d), // данные могут быть 'строкой' или {объектом}!
    //                     headers: {
    //                         'Accept': "application/json",
    //                         'Content-Type': "application/json",
    //
    //                     },
    //                     credentials: 'include',
    //                 }).then((response) => response.json())
    //     );
    //
    //     if (isLoading) return <p>Загрузка...</p>;
    //
    //     if (error) return <p>Ошибка: {error.message}</p>;
    //     props.setauth(true)
    //     return true;
    //
    // }



    return (
        <div>
        <section className="position-relative py-4 py-xl-5">
            <div className="container">
                <div className="row d-flex justify-content-center">
                    <div className="col-md-6 col-xl-4">
                        <div className="card mb-5">
                            <div className="card-body d-flex flex-column align-items-center">
                                <form className="text-center" method={"POST"} onSubmit={loginHandler}>
                                    <div className="mb-3"><input className="form-control" type="text" name="login"
                                                                 placeholder="Login" value={login} onChange={loginChangeHandler}/>
                                    </div>
                                    <div className="mb-3"><input className="form-control" type="password"
                                                                 name="password"
                                                                 placeholder="Password" value={pas} onChange={pasChangeHandler}/></div>
                                    <div className="mb-3">
                                        <button className="btn btn-primary d-block w-100" type="submit">Login</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
        </div>
    );
}


export default Login;
